// Windows Agent Count by Solutions For OMS
//cluster('genevaagent.kusto.windows.net').database('Telemetry').
let HBMonthly = union
    cluster('genevaagent.kusto.windows.net').database('Telemetry').LinuxDailySnapshot,
    cluster('genevaagent.kusto.windows.net').database('Telemetry').WindowsDailySnapshot
| summarize HeartbeatSum = sum(HeartbeatCount) by Date
| summarize HeartbeatMonthlyMax = max(HeartbeatSum) by startofmonth(Date);
HBMonthly
