tiles.query:
//WAD Version breakdown
//WAD Pop by agent version
let Newest = toscalar(cluster('genevaagent.kusto.windows.net').database('Telemetry').WADDailySnapshot
    | project Date
    | top 1 by Date);
cluster('genevaagent.kusto.windows.net').database('Telemetry').WADDailySnapshot
//| where Date between ((Newest - 5m) .. Newest)
| summarize WinAgentCount = dcountif(ResourceId, OsType == "windows") by Version
| where WinAgentCount > 0
| sort by WinAgentCount

//LAD Version breakdown
//LAD Pop by agent version
let Newest = toscalar(cluster('genevaagent.kusto.windows.net').database('Telemetry').LADDailySnapshot
    | project Date
    | top 1 by Date);
cluster('genevaagent.kusto.windows.net').database('Telemetry').LADDailySnapshot
//| where Date between ((Newest - 5m) .. Newest)
| summarize LnxAgentCount = dcountif(ResourceId, OsType == "linux") by Version
| where LnxAgentCount > 0
| sort by LnxAgentCount
